        public string GeneratePDF(string DocName, string SavePath, string HtmlBody)
        {

            //string base64 = @"";
            iTextSharp.text.Document pdfDoc = new iTextSharp.text.Document(PageSize.LETTER, 20f, 20f, 20f, 40f);
            string BaseDir = AppDomain.CurrentDomain.BaseDirectory + "//CheckPDF//";
            String PackaginglistPDFPath = BaseDir + "//" + DocName + ".pdf";
            //  String SSCreateLablePath = BaseDir + SavePath + DocName + "_ShipLable.pdf";
            // String MergedFile = BaseDir + SavePath + DocName + "_ShipLable_Merge.pdf";
            string baseURLpath = SavePath + "//" + DocName + ".pdf";
            DirectoryInfo dtIfo = Directory.CreateDirectory(BaseDir + SavePath);

            //////////Save Thriii Packaging list PDF
            if (dtIfo.Exists)
            {

                PdfWriter pw = PdfWriter.GetInstance(pdfDoc, new FileStream(PackaginglistPDFPath, FileMode.Create));
                pw.PageEvent = new ITextEvents();
                pdfDoc.Open();
                StringReader cont = new System.IO.StringReader(HtmlBody);
                XMLWorkerHelper.GetInstance().ParseXHtml(pw, pdfDoc, cont);
                pdfDoc.Close();
            }

            return baseURLpath.Replace("\\", "/");
        }
        public string GeneratePDF_Check(string DocName, string SavePath, string HtmlBody)
        {

            string BaseDir = AppDomain.CurrentDomain.BaseDirectory + "//CheckPDF//";
            String PackaginglistPDFPath = BaseDir + "//" + SavePath + "//" + DocName + ".pdf";
            string baseURLpath = SavePath + "//" + DocName + ".pdf";
            iTextSharp.text.Document pdfDoc = new iTextSharp.text.Document(PageSize.LETTER, 20f, 20f, 20f, 0f);

            try
            {
                //string base64 = @"";
                DirectoryInfo dtIfo = Directory.CreateDirectory(BaseDir + SavePath);

                //////////Save Thriii Packaging list PDF
                if (dtIfo.Exists)
                {

                    PdfWriter pw = PdfWriter.GetInstance(pdfDoc, new FileStream(PackaginglistPDFPath, FileMode.Create));
                    pw.PageEvent = new ITextEvents();
                    pdfDoc.Open();
                    StringReader cont = new System.IO.StringReader(HtmlBody);
                    XMLWorkerHelper.GetInstance().ParseXHtml(pw, pdfDoc, cont);
                    pw.UserProperties = true;
                    pdfDoc.Close();
                }
            } catch (Exception ex)
            {
                pdfDoc.Close();
                return ex.ToString();
            }

            return (BaseDir + baseURLpath).Replace("\\", "/");
        }

