        public string ReportPDFGenerateFolder(string FolderName, string DocName, string SavePath, string HtmlBody, Boolean ReturnFullPath = false)
        {
            try
            {

                DocName += "_" + Guid.NewGuid().ToString();
                iTextSharp.text.Document pdfDoc = new iTextSharp.text.Document(PageSize.LETTER.Rotate(), 10f, 10f, 10f, 50f);
                string BaseDir = AppDomain.CurrentDomain.BaseDirectory + FolderName + "\\";
                String PackaginglistPDFPath = BaseDir + "\\" + DocName + ".pdf";
                PackaginglistPDFPath = PackaginglistPDFPath.Replace("//", "\\");

                string baseURLpath = SavePath + "//" + DocName + ".pdf";
                if (ReturnFullPath == true) { baseURLpath = FolderName + "/" + DocName + ".pdf"; };
                DirectoryInfo dtIfo = Directory.CreateDirectory(BaseDir + SavePath);

                if (dtIfo.Exists)
                {
                    PdfWriter pw = PdfWriter.GetInstance(pdfDoc, new FileStream(PackaginglistPDFPath, FileMode.Create));
                    pw.PageEvent = new ITextEvents();
                    pw.SetFullCompression();
                    pdfDoc.Open();
                    StringReader cont = new System.IO.StringReader(HtmlBody);
                    XMLWorkerHelper.GetInstance().ParseXHtml(pw, pdfDoc, cont);
                    pdfDoc.Close();
                }

                return baseURLpath.Replace("\\", "/");
            }
            catch (Exception ex)
            {
                return "";
            }
        }


        public string ReportPDFGenerateFolderT(string FolderName, string DocName, string SavePath, string HtmlBody, Boolean ReturnFullPath = false)
        {
            try
            {

                DocName += "_" + Guid.NewGuid().ToString();
                iTextSharp.text.Document pdfDoc = new iTextSharp.text.Document(PageSize.LETTER.Rotate(), 10f, 10f, 10f, 50f);
                string BaseDir = AppDomain.CurrentDomain.BaseDirectory + FolderName + "\\";
                String PackaginglistPDFPath = BaseDir + "\\" + DocName + ".pdf";
                PackaginglistPDFPath = PackaginglistPDFPath.Replace("//", "\\");

                string baseURLpath = SavePath + "//" + DocName + ".pdf";
                if (ReturnFullPath == true) { baseURLpath = FolderName + "/" + DocName + ".pdf"; };
                DirectoryInfo dtIfo = Directory.CreateDirectory(BaseDir + SavePath);

                if (dtIfo.Exists)
                {
                    PdfWriter pw = PdfWriter.GetInstance(pdfDoc, new FileStream(PackaginglistPDFPath, FileMode.Create));
                    pw.PageEvent = new ITextEvents();
                    pdfDoc.Open();
                    StringReader cont = new System.IO.StringReader(HtmlBody);
//                    XMLWorkerHelper.GetInstance().ParseXHtml(pw, pdfDoc, cont);
//                    pdfDoc.Close();

                    //create the default CSS Resolver
                    XMLWorkerHelper helperInstance = XMLWorkerHelper.GetInstance();
                    CssFilesImpl cssFiles = new CssFilesImpl();
                    cssFiles.Add(helperInstance.GetDefaultCSS());
                    StyleAttrCSSResolver cssRevolver = new StyleAttrCSSResolver(cssFiles);

                    //create the default Font provider
                    XMLWorkerFontProvider xmlWorkerFontProvider = new XMLWorkerFontProvider();
                    HtmlPipelineContext htmlContext = new HtmlPipelineContext(new CssAppliersImpl(xmlWorkerFontProvider));

                    //create the default tag provider
                    ITagProcessorFactory tpf = Tags.GetHtmlTagProcessorFactory();
                    htmlContext.SetTagFactory(tpf);

                    //set the image provider, in my case a blank image provider as my docs have no images
                    htmlContext.SetImageProvider(new BlankImageProvider(pdfDoc));

                    //create the Pipeline
                    IPipeline pipeline = new CssResolverPipeline(cssRevolver, new
                       HtmlPipeline(htmlContext, new PdfWriterPipeline(pdfDoc, pw)));
                    //parse the XHTML
                    XMLWorker worker = new XMLWorker(pipeline, true);
                    XMLParser p = new XMLParser(worker);
                    p.Parse(cont);
                    pdfDoc.Close();

                }

                return baseURLpath.Replace("\\", "/");
            }
            catch (Exception ex)
            {
                return "";
            }
        }

